package com.samples.divya;

public class FindEvenNumber {

	/**
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int number=10;
		
		System.out.println("the even numbers between 1 to 10 are:");

		for(int i=0;i<=10;i++){
			
			if(i %2 == 0){
				System.out.println(i+" ");
			}
			
		}
	}

}
